import React from 'react';
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from 'react-router-dom';
import { removeFromCart, clearCart } from "../Redux/cartSlice";
import './Cart.css';

const Cart = () => {
  const cartItems = useSelector((state) => state.cart);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  // Function to remove an item from the cart
  const handleRemoveItem = (id) => {
    dispatch(removeFromCart(id)); // Dispatch the action to remove the item
  };

  // Function to clear all items from the cart
  const handleClearCart = () => {
    dispatch(clearCart()); // Dispatch the action to clear the cart
  };

  // Calculate the total price of the cart
  const calculateTotalPrice = () => {
    return cartItems
      .reduce((total, item) => {
        const price = parseFloat(item.Price); 
        const quantity = parseInt(item.Quantity, 10);

        if (isNaN(price) || isNaN(quantity)) {
          return total; 
        }
        return total + price * quantity;
      }, 0)
      .toFixed(2);  
  };

  const handleCheckout = () => {
    navigate('/checkout'); 
  };

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
          {cartItems.map((item) => (
            <div key={item.id} className="cart-item">
              <h4>{item.Name}</h4>
              <img src={item.Image} alt={item.Name} className="cart-item-image" />
              <p>Price: R{item.Price}</p>
              <p>Quantity: {item.Quantity}</p>
              <p>Total: R{(item.Price * item.Quantity).toFixed(2)}</p>
              <button
                onClick={() => handleRemoveItem(item.id)}
                className="remove-button"
              >
                Remove
              </button>
            </div>
          ))}
          <h3>Total Price: R{calculateTotalPrice()}</h3>
          <button onClick={handleCheckout} className="checkout-button">
            Proceed to Checkout
          </button>
          <button onClick={handleClearCart} className="clear-cart-button">
            Clear Cart
          </button>
        </>
      )}
    </div>
  );
};

export default Cart;
